import { withRouter } from 'next/router'
import { useState } from 'react'
// typically you want to use `next/link` for this usecase
// but this example shows how you can also access the router
// using the withRouter utility.

function ActiveLink({ children, router, href }) {

    const [forceRender, setForceRender] = useState("blah")

    const style = {
        boxShadow: router.pathname === href ? 'inset 0 -9px 0px white;' : '',
    }

    const handleClick = e => {
        e.preventDefault()
        router.push(href)
        setForceRender("blah");
    }

    return (
        <a className="nav__link gg-u-text-whisper" href={href} onClick={handleClick} style={style}>
            {children}
        </a>
    )
}

export default withRouter(ActiveLink)
